window.addEventListener("load",(()=>{let radios=document.querySelectorAll('#mode_faq input[type="radio"]');function switchState(event){radios.forEach((input=>{input.parentElement.setAttribute("class","btn btn-default")}));event.target.parentElement.setAttribute("class","btn btn-main-theme")}radios.forEach((input=>{input.addEventListener("click",(e=>switchState(e)));input.addEventListener("change",(e=>switchState(e)));input.addEventListener("input",(e=>switchState(e)))}))}));